const raw = $input.first().json.content;

let parsed;
let markdown;

try {
  // Step 1: Strip code block
  const cleaned = raw.replace(/```json\n|```/g, '').trim();

  // Step 2: Parse JSON
  parsed = JSON.parse(cleaned);

  // Step 3: Build Markdown summary
  markdown = `
**Invoice Summary**

- **Invoice #:** ${String(parsed.invoice_number || 'N/A')}
- **Vendor:** ${String(parsed.vendor || 'N/A')}
- **Date:** ${String(parsed.date || 'N/A')}
- **Due Date:** ${String(parsed.due_date || 'N/A')}
- **Description:** ${String(parsed.description || 'N/A')}
- **Subtotal:** $${parsed.subtotal != null ? parsed.subtotal.toFixed(2) : '0.00'}
- **Tax:** $${parsed.tax != null ? parsed.tax.toFixed(2) : '0.00'}
- **Total:** $${parsed.total != null ? parsed.total.toFixed(2) : '0.00'}
- **Status:** ${String(parsed.status || 'N/A')}
`.trim();

} catch (error) {
  throw new Error("Failed to parse JSON from OpenAI output: " + error.message);
}

return [{
  json: {
    ...parsed,
    markdown
  },
  binary: $input.first().binary // üëà This keeps the file intact
}];
