// Function to generate a random phone number
function generatePhone() {
    return Math.floor(Math.random() * 9000000000 + 1000000000).toString();
}

// Function to generate a random email
function generateEmail(firstName, lastName) {
    const domains = ['gmail.com', 'yahoo.com', 'outlook.com', 'company.com'];
    const domain = domains[Math.floor(Math.random() * domains.length)];
    return `${firstName.toLowerCase()}.${lastName.toLowerCase()}@${domain}`;
}

// List of sample first names and last names
const firstNames = ['John', 'Jane', 'Michael', 'Emily', 'David', 'Sarah', 'James', 'Emma', 'William', 'Olivia'];
const lastNames = ['Smith', 'Johnson', 'Williams', 'Brown', 'Jones', 'Garcia', 'Miller', 'Davis', 'Rodriguez', 'Martinez'];
const cities = ['New York', 'Los Angeles', 'Chicago', 'Houston', 'Phoenix', 'Philadelphia', 'San Antonio', 'San Diego', 'Dallas', 'San Jose'];
const jobTitles = ['Software Engineer', 'Product Manager', 'Sales Director', 'Marketing Manager', 'CEO', 'CTO', 'CFO', 'HR Manager'];

// Generate random person data
const firstName = firstNames[Math.floor(Math.random() * firstNames.length)];
const lastName = lastNames[Math.floor(Math.random() * lastNames.length)];
const city = cities[Math.floor(Math.random() * cities.length)];
const jobTitle = jobTitles[Math.floor(Math.random() * jobTitles.length)];

return [
  {
    json: {
      data: {
        name: {
          firstName,
          lastName
        },
        emails: {
          primaryEmail: generateEmail(firstName, lastName)
        },
        phones: {
          primaryPhoneNumber: generatePhone(),
          primaryPhoneCountryCode: 'US',
          primaryPhoneCallingCode: '1'
        },
        city,
        jobTitle
      }
    }
  }
];
